import Popup from './components/Popup';
import './App.css';
import React, { useState, useEffect} from 'react'


function App() {
  const [buttonPopup, setButtonPopup] = useState(false);
  const [timedPopup, setTimedPopup] = useState(false);

  useEffect(() => {
    setTimeout(() => {
      setTimedPopup(true);
    }, 3000);
  }, []);

  return (
    <div className="App">
      <main>
        <h1>React Popups</h1>
        <button
          className="btn"
          onClick={() => setButtonPopup(true)}>Open
          Popup
        </button>
      </main>


      <Popup
        trigger={buttonPopup}
        setTrigger={setButtonPopup}>
        <h3>My Popup</h3>
      </Popup>
    </div>
  );
}

export default App;
