import Popup from './components/Popup';
import './App.css';
import React, { useState, useEffect} from 'react'


function App() {
  const [buttonPopup, setButtonPopup] = useState(false);
  const [timedPopup, setTimedPopup] = useState(false);

  useEffect(() => {
    setTimeout(() => {
      setTimedPopup(true);
    }, 3000);
  }, []);

  return (
    <div className="App">
      <main>
        <div className="container">
          <h1>Glassmorphisim Popup Created in ReactJS</h1>
          <button
            className="btn"
            onClick={() => setButtonPopup(true)}>Open
            Popup
          </button>
        </div>

      </main>
      <Popup
        trigger={buttonPopup}
        setTrigger={setButtonPopup}>
        <h3>My Popup</h3>
      </Popup>
    </div>
  );
}

export default App;
